        vm.findOne= function(cond, addresses){
            var address;
            for (var i=0; i<addresses.length; i++){
                var matched= true;
                for (var key in cond){
                    if (addresses[i][key] !== cond[key]){
                        matched= false;
                        break;
                    }
                }
                if (matched){
                    address= addresses[i];
                    address.index= i;
                    break;
                }
            }
            return address;
        };
